var utils = new Nutanix_Calm_Utils();
var logger = new Nutanix_Calm_Logger();

var userId = workflow.scratchpad.userID+'';
var actSysid = current.actionsysid + '';
var actionResp = '';
var runlog_uuid = '';
var app_uuid = '';
var action_uuid = '';
var action_vars = '';
var action_name = '';

var actionObj = new GlideRecord('x_nuta2_nutanix_ca_nutanix_calm_action');
actionObj.addQuery('sys_id', actSysid);
actionObj.query();
if(actionObj.next()) {
	app_uuid = actionObj.parent_uuid + '';
	action_uuid = actionObj.uuid + '';
	action_vars = current.action_json + '';
	action_name = actionObj.name + '';
}
var appResp = utils.makeCalmApiCall('GET', '/apps/'+app_uuid, '');
var parsedAppResp = JSON.parse(appResp.getBody()).metadata;

var actBody = '{"api_version":"3.0","metadata":{"project_reference":{"kind":"'+ parsedAppResp.project_reference.kind+''+'","name":"'+ parsedAppResp.project_reference.name+''+'","uuid":"'+ parsedAppResp.project_reference.uuid+''+'"},"name":"'+parsedAppResp.name+''+'","creation_time":"'+parsedAppResp.creation_time+''+'","spec_version":'+ parsedAppResp.spec_version+''+',"kind":"'+parsedAppResp.kind+''+'","last_update_time":"'+parsedAppResp.last_update_time+''+'"},"spec":{"target_uuid":"'+app_uuid+''+'","target_kind":"Application","args":['+action_vars+''+']}}';

var parsedActBody = JSON.parse(actBody);
logger.info("Performing Action for "+action_name+'');

if(action_name+'' == 'action_delete') {
	actionResp = utils.makeIntermediateApiCall('DELETE', '/apps/'+app_uuid, '', userId+'');
	runlog_uuid = JSON.parse(actionResp.getBody()).status.runlog_uuid;
	
}
else if(action_name+'' == 'action_soft_delete') {
	actionResp = utils.makeIntermediateApiCall('DELETE', '/apps/'+ app_uuid + '?type=soft', '', userId+'');
	runlog_uuid = JSON.parse(actionResp.getBody()).status.runlog_uuid;
	
}
else {
	actionResp = utils.makeIntermediateApiCall('POST', '/apps/' + app_uuid + '/actions/' + action_uuid+'' + '/run', JSON.stringify(parsedActBody), userId+'');
	runlog_uuid = JSON.parse(actionResp.getBody()).status.runlog_uuid;
}

if(actionResp.getStatusCode() == '200' || actionResp.getStatusCode() == '201') {
	
	var gr = new GlideRecord('x_nuta2_nutanix_ca_nutanix_calm_application');
	gr.initialize();
	gr.addQuery('uuid', app_uuid);
	gr.query();
	if(gr.next()) {
		gr.action_flag = '1';
		gr.update();
	}
	
	var runlogResp = utils.makeCalmApiCall('GET', '/apps/' + app_uuid + '/app_runlogs/' + runlog_uuid, '');
	var actionState = JSON.parse(runlogResp.getBody()).status.state + '';
	
	workflow.scratchpad.actionState = actionState + '';
	workflow.scratchpad.app_uuid = app_uuid + '';
	workflow.scratchpad.runlog_uuid = runlog_uuid + '';
	workflow.scratchpad.action_name = action_name + '';
	
} else {
	
	logger.error("Application action failed: "+JSON.stringify(runlogResp));
	
}

