var utils = new Nutanix_Calm_Utils();
var logger = new Nutanix_Calm_Logger();

var app_uuid = workflow.scratchpad.app_uuid;
var runlog_uuid = workflow.scratchpad.runlog_uuid;

var runlogResp = utils.makeCalmApiCall('GET', '/apps/' + app_uuid + '/app_runlogs/' + runlog_uuid, '');

var parsedJson = JSON.parse(runlogResp.getBody());
utils.insertAppRunlogs(parsedJson); // calling "insertAppRunlogs" method to insert run logs into app run logs table

logger.info("Perform action is completed, Check app runlogs related list on Application");

var gr = new GlideRecord('x_nuta2_nutanix_ca_nutanix_calm_application');
gr.initialize();
gr.addQuery('uuid', app_uuid);
gr.query();
if(gr.next()) {
	gr.action_flag = '';
	gr.update();
}

var actionName = workflow.scratchpad.action_name;

//Inserting new created apps details into [x_nuta2_nutanix_ca_nutanix_calm_application] table
var appUuidResponse = utils.makeCalmApiCall('GET', '/apps/'+app_uuid, '');

if(appUuidResponse.getStatusCode() == '200' || appUuidResponse.getStatusCode() == '201'){
	
	var appParsed = JSON.parse(appUuidResponse.getBody());
	
	var date = new Date(appParsed.metadata.last_update_time/1000);		
	var year = date.getUTCFullYear();
	var month = date.getUTCMonth()+1; // getMonth() is zero-indexed, so we'll increment to get the correct month number
	var day = date.getUTCDate();
	var hours = date.getUTCHours();
	var minutes = date.getUTCMinutes();
	var seconds = date.getUTCSeconds();
	var lastUpdate = year + '-' + month + '-' + day + ' ' + hours + ':' + minutes+ ':' +seconds;
	
	var appState = appParsed.status.state;
	
	var grApp = new GlideRecord('x_nuta2_nutanix_ca_nutanix_calm_application');
	grApp.addQuery('uuid', app_uuid);
	grApp.query();	
	if(grApp.next()){
		grApp.state = appState;
		grApp.last_update_time = lastUpdate;
		grApp.update();				
	}
	
} else {
	logger.error("Error during retrieval of application on '"+ actionName+"' actions to API /apps/<uuid> Error : "+appUuidResponse.getBody());
}

// Deleting records of 'App Action Workflow Staging Table' for clean up
var gr = new GlideRecord('x_nuta2_nutanix_ca_app_action_workflow_trigger');
gr.query();
gr.setWorkflow(false);
gr.deleteMultiple();

